import 'package:core/core.dart';import 'package:driver/features/auth/domain/entities/driver_info.dart';import 'package:network/network.dart';import '../../../../app_injection.dart';import '../../../../common/utils/helper_method.dart';import '../../data/repositories/auth_repository.dart';class AuthUseCase {  AuthUseCase({required AuthRepository repository}) : _repository = repository;  final AuthRepository _repository;  Future<ApiResult<DriverInfo>> login({    required String email,    required String password,    required String deviceToken,    bool rememberMe = true,  }) async {    return onResult<DriverInfo>(      await _repository.login(        username: email,        password: password,        deviceToken: deviceToken,        rememberMe: rememberMe,      ),      onData: (data) {        si<SStorage>().set(key: kAccessToken, value: data.token);      },    );  }}