import 'package:driver/app_injection.dart';import 'package:driver/features/auth/data/data_sources/auth_api.dart';import 'package:driver/features/auth/data/repositories/auth_repository.dart';import 'package:driver/features/auth/domain/use_cases/auth_usecase.dart';import 'package:driver/features/auth/presentation/manager/auth/bloc.dart';class AuthInjection {  static void dependencies() {    si.registerLazySingleton(      () => AuthApi(api: si()),    );    si.registerLazySingleton(      () => AuthRepository(connectivity: si(), remote: si()),    );    si.registerLazySingleton(      () => AuthUseCase(repository: si()),    );    si.registerLazySingleton(() => AuthBloc(si()));  }}