import 'package:core/core.dart';import 'package:design/design.dart';import 'package:driver/features/auth/presentation/manager/auth/bloc.dart';import 'package:driver/features/auth/presentation/manager/auth/state.dart';import '../../../../../common/utils/bloc_wrapper.dart';class LoginContainer extends StatelessWidget {  final ViewModelBuilder<dynamic> builder;  const LoginContainer({Key? key, required this.builder}) : super(key: key);  @override  Widget build(BuildContext context) {    return BlocSelectorWrapper<dynamic, AuthBloc, AuthState>(      initChild: builder(context, {}),      onRetry: () => context.read<AuthBloc>().retry(),      selector: (state) => state.loginState!,      builder: builder,    );  }}