import 'package:driver/app_injection.dart';import 'package:driver/features/history/data/data_sources/history_api.dart';import '../data/repositories/history_repositories.dart';import '../domain/use_cases/history_usecase.dart';import '../presentation/manager/history/bloc.dart';class HistoryInjection {  static void dependencies() {    si.registerLazySingleton(      () => HistoryApi(api: si()),    );    si.registerLazySingleton(      () => HistoryRepository(connectivity: si(), remote: si()),    );    si.registerLazySingleton(      () => HistoryUseCase(repository: si()),    );    si.registerLazySingleton(() => HistoryBloc(si()));  }}