import 'package:driver/features/data/data_sources/driver_api.dart';import 'package:driver/features/data/repositories/driver_repository.dart';import 'package:driver/features/presentation/manager/bloc.dart';import 'package:internet_connection_checker/internet_connection_checker.dart';import '../../app_injection.dart';import '../../common/utils/connectivity.dart';import '../domain/use_cases/driver_usecase.dart';class DriverInjection {  static void dependencies() {    si.registerLazySingleton(      () => DriverApi(api: si()),    );    si.registerLazySingleton(      () => DriverRepository(connectivity: si(), remote: si()),    );    si.registerLazySingleton(      () => DriverUseCase(repository: si()),    );    si.registerLazySingleton(() => DriverBloc(si()));  }}