import 'package:driver/features/invoice/domain/repositories/i_invoice_repository.dart';import 'package:network/network.dart';import '../../../../common/utils/connectivity.dart';import '../../../../common/utils/helper_method.dart';import '../data_sources/balance_api.dart';class InvoiceRepository extends IInvoiceRepository {  final SConnectivity _connectivity;  final InvoiceApi _remote;  InvoiceRepository({    required SConnectivity connectivity,    required InvoiceApi remote,  })  : _connectivity = connectivity,        _remote = remote;  @override  Future<ApiResult<dynamic>> getInvoices() async {    return await fetchApiResult<dynamic>(      isConnected: _connectivity.isConnected,      fetch: _remote.getInvoices(),    );  }  Future<ApiResult<dynamic>> getStatistics() async {    return await fetchApiResult<dynamic>(      isConnected: _connectivity.isConnected,      fetch: _remote.getStatistics(),    );  }}