import 'package:network/network.dart';import '../../../../common/utils/helper_method.dart';import '../../data/repositories/invoices_repository.dart';class InvoiceUseCase {  InvoiceUseCase({required InvoiceRepository repository}) : _repository = repository;  final InvoiceRepository _repository;  Future<ApiResult<dynamic>> getInvoices() async {    return onResult<dynamic>(      await _repository.getInvoices(),      onData: (data) {},    );  }  Future<ApiResult<dynamic>> getStatistics() async {    return onResult<dynamic>(      await _repository.getStatistics(),      onData: (data) {},    );  }}