import 'dart:convert';import 'package:core/core.dart';import 'package:driver/common/utils/helper_method.dart';import 'package:either_dart/either.dart';import 'package:network/network.dart';import '../../domain/entities/profile.dart';class ProfileApi {  ProfileApi({required SHttpClient api}) : _api = api;  final SHttpClient _api;  Future<Either<SResponseException, dynamic>> getProfile() {    return fetch(call: () async {      final response = await _api.get<dynamic>(        'api/DriverApp/Profile',      );      return DriverProfile.fromJson(response.data);    });  }}