import 'package:driver/features/profile/data/data_sources/profile_api.dart';import 'package:driver/features/profile/domain/repositories/i_profile_repository.dart';import 'package:network/src/s_api_result.dart';import '../../../../common/utils/connectivity.dart';import '../../../../common/utils/helper_method.dart';class ProfileRepository extends IProfileRepository {  final ProfileApi _remote;  final Connectivity _connectivity;  ProfileRepository({required ProfileApi remote, required Connectivity connectivity})      : _remote = remote,        _connectivity = connectivity;  @override  Future<ApiResult> getProfile() async {    return await fetchApiResult<dynamic>(      isConnected: _connectivity.isConnected,      fetch: _remote.getProfile(),    );  }}