import 'package:driver/app_injection.dart';import 'package:driver/features/profile/data/data_sources/profile_api.dart';import 'package:driver/features/profile/data/repositories/profile_repository.dart';import 'package:driver/features/profile/domain/use_cases/profile_usecase.dart';import 'package:driver/features/profile/presentation/manager/profile/bloc.dart';class ProfileInjection {  static void dependencies() {    si.registerLazySingleton(      () => ProfileApi(api: si()),    );    si.registerLazySingleton(      () => ProfileRepository(connectivity: si(), remote: si()),    );    si.registerLazySingleton(      () => ProfileUseCase(repository: si()),    );    si.registerLazySingleton(() => ProfileBloc(si()));  }}