import 'package:core/core.dart';import 'package:design/design.dart';import 'package:driver/features/auth/presentation/manager/auth/bloc.dart';import 'package:driver/features/auth/presentation/manager/auth/state.dart';import 'package:driver/features/profile/presentation/manager/profile/bloc.dart';import 'package:driver/features/profile/presentation/manager/profile/event.dart';import 'package:driver/features/profile/presentation/manager/profile/state.dart';import '../../../../../common/utils/bloc_wrapper.dart';import '../../../domain/entities/profile.dart';class ProfileContainer extends StatelessWidget {  final ViewModelBuilder<DriverProfile> builder;  const ProfileContainer({Key? key, required this.builder}) : super(key: key);  @override  Widget build(BuildContext context) {    return BlocSelectorWrapper<DriverProfile, ProfileBloc, ProfileState>(      onInit: () {        context.read<ProfileBloc>().add(GetProfileEvent());      },      onRetry: () => context.read<ProfileBloc>().retry(),      selector: (state) => state.infoState!,      builder: builder,    );  }}