import 'package:either_dart/either.dart';import 'package:network/network.dart';import '../../../../common/utils/helper_method.dart';class SupportApi {  SupportApi({required SHttpClient api}) : _api = api;  final SHttpClient _api;  Future<Either<SResponseException, dynamic>> addSupportText(String text) {    return fetch(call: () async {      final response = await _api.post<dynamic>(        'api/DriverApp/AddSupport',        data: {'description': text},      );      return response.data;      // return DriverProfile.fromJson(response.data);    });  }}