import 'package:network/network.dart';import '../../../../common/services/connectivity.dart';import '../../../../common/utils/connectivity.dart';import '../../../../common/utils/helper_method.dart';import '../../domain/repositories/i_support_repository.dart';import '../data_sources/support_api.dart';class SupportRepository extends ISupportRepository {  final SConnectivity _connectivity;  final SupportApi _remote;  SupportRepository({    required SConnectivity connectivity,    required SupportApi remote,  })  : _connectivity = connectivity,        _remote = remote;  @override  Future<ApiResult<dynamic>> addSupport(String text) async {    return await fetchApiResult<dynamic>(      isConnected: _connectivity.isConnected,      fetch: _remote.addSupportText(text),    );  }}