import 'package:network/network.dart';import '../../../../common/utils/helper_method.dart';import '../../data/repositories/support_repository.dart';class SupportUseCase {  SupportUseCase({required SupportRepository repository}) : _repository = repository;  final SupportRepository _repository;  Future<ApiResult<dynamic>> addSupport(String text) async {    return onResult<dynamic>(      await _repository.addSupport(text),      onData: (data) {},    );  }}