import 'package:core/core.dart';import 'package:design/design.dart';import 'package:driver/features/auth/presentation/manager/auth/bloc.dart';import 'package:driver/features/auth/presentation/manager/auth/state.dart';import 'package:driver/features/support/presentation/manager/support/bloc.dart';import 'package:driver/features/support/presentation/manager/support/state.dart';import '../../../../../common/utils/bloc_wrapper.dart';class SupportContainer extends StatelessWidget {  final ViewModelBuilder<dynamic> builder;  const SupportContainer({Key? key, required this.builder}) : super(key: key);  @override  Widget build(BuildContext context) {    return BlocSelectorWrapper<dynamic, SupportBloc, SupportState>(      initChild: builder(context, {}),      onRetry: () => context.read<SupportBloc>().retry(),      selector: (state) => state.supportState!,      builder: builder,    );  }}