import 'package:core/core.dart';import 'package:design/design.dart';import '../../generated/assets.dart';import '../widgets/lottie_widget.dart';class BlocSelectorWrapper<T, B extends StateStreamable<S>, S>    extends StatelessWidget {  final ViewModelBuilder<T> builder;  final BlocWidgetSelector<S, SBlocState> selector;  final Function() onRetry;  final Widget? initChild;  final Widget? loading;  final Function()? onInit;  /// TODO need to rename the props here  const BlocSelectorWrapper({    Key? key,    required this.builder,    required this.selector,    required this.onRetry,    this.onInit,    this.initChild,    this.loading,  }) : super(key: key);  @override  Widget build(BuildContext context) {    return BlocContainerSelector<T, B, S>(        onRetry: onRetry,        onInit: onInit,        selector: selector,        initChild: initChild,        builder: builder,        errorBuilder: (_, error, retry) {          return SErrorScreen(            retry: retry,            message: error,            error: error,            retryText: 'Retry',            svgPath: Assets.iconsGroup7,          );        },        loadingWidget: loading ?? const LottieLoading());  }}