import 'package:rider/features/data/data_sources/rider_api.dart';import 'package:rider/features/data/repositories/rider_repository.dart';import 'package:rider/features/domain/use_cases/rider_usecase.dart';import 'package:rider/features/presentation/manager/bloc.dart';import 'package:rider/main.dart';class RiderInjection {  static void dependencies() {    si.registerLazySingleton(      () => RiderApi(api: si()),    );    si.registerLazySingleton(      () => RiderRepository(connectivity: si(), remote: si()),    );    si.registerLazySingleton(      () => RiderUseCase(repository: si()),    );    si.registerLazySingleton(() => RiderBloc(si()));  }}