//for dev environment use: flutter run --flavor=dev --dart-define=app.flavor=dev//for prod environment use: flutter run --flavor=prod --dart-define=app.flavor=prod/// File created by/// Joelle <Joelle-supy-io> && Abed <Abed-supy-io>/// on 28 /Apr/2022part of '../utils.dart';class SConfigReader {  static String getEnvironment({String? defaultValue}) {    String flavor = String.fromEnvironment(      'app.flavor',      defaultValue: defaultValue ?? 'prod',    );    return flavor;  }}